er=\"0px 0px 0px 0px\" border_color=\"rgb(235, 235, 235)\"]\n\n[row h_align=\"center\"]\n\n[col span=\"10\" span__sm=\"12\" align=\"center\"]\n\n[ux_text text_color=\"rgb(255, 255, 255)\"]\n\n<p> </p>\n<h2 class=\"uppercase\">nhận ưu đãi ngay</h2>\n<p>Đó là lựa chọn tuyệt vời và bạn sẽ có những trải nghiệm tuyệt vời</p>\n[/ux_text]\n[button text=\"MỞ XEM NGAY\" color=\"white\"]\n\n\n[/col]\n\n[/row]\n\n[/section]\n[section label=\"Simple Center\" bg_color=\"rgb(245, 245, 245)\" padding=\"0px\" height=\"300px\"]\n\n[ux_banner_grid spacing=\"collapse\" height=\"587\" width=\"full-width\"]\n\n[col_grid span=\"6\"]\n\n[ux_banner height=\"400px\" bg=\"63\" bg_overlay=\"rgba(0, 0, 0, 0.2)\" hover=\"zoom\"]\n\n[text_box text_color=\"dark\" width=\"100\" scale=\"123\" margin=\"0px 0px -60px 0px\" padding=\"14px 85px 11px 30px\" position_x=\"50\" position_y=\"90\" text_align=\"left\" bg=\"rgba(45, 150, 205, 0.8)\"]\n\n<h3 class=\"uppercase\"><strong>NEW Summer Trends</strong></h3>\n\n[/text_box]\n\n[/ux_banner]\n\n[/col_grid]\n[col_grid span=\"3\" height=\"1-2\"]\n\n[ux_banner height=\"400px\" bg=\"126\" bg_overlay=\"rgba(0, 0, 0, 0.2)\"]\n\n[text_box text_color=\"dark\" width=\"100\" scale=\"35\" margin=\"0px 0px -30px 0px\" padding=\"4px 30px 8px 0px\" position_x=\"50\" position_y=\"90\" bg=\"rgba(72, 150, 196, 0.94)\" border=\"0px 0px 0px 0px\"]\n\n[ux_text font_size=\"0.75\" line_height=\"1.75\" text_align=\"left\"]\n\n<h4 class=\"uppercase\">Hơn một chiếc xe</h4>\n[/ux_text]\n\n[/text_box]\n\n[/ux_banner]\n\n[/col_grid]\n[col_grid span=\"3\" height=\"1-2\"]\n\n\n[/col_grid]\n[col_grid span=\"3\" height=\"1-2\"]\n\n\n[/col_grid]\n[col_grid span=\"3\" height=\"1-2\"]\n\n\n[/col_grid]\n\n[/ux_banner_grid]\n\n[/section]\n[block id=\"95\"]\n\n\n<!-- /wp:flatsome/uxbuilder -->', 'Home', '', 'inherit', 'closed', 'closed', '', '39-revision-v1', '', '', '2022-05-16 13:28:02', '2022-05-16 13:28:02', '', 39, 'http://localhost:8080/prjCar/?p=228', 0, 'revision', '', 0);
